import React, { useState } from "react";
import "boxicons/css/boxicons.min.css";
import "../../styles/authstyle.css"; // Ensure correct path

const Login = () => {
  const [showPassword, setShowPassword] = useState(false);

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h2 className="auth-title">Hello Again!</h2>
        <p className="auth-description">
          Let's get started with uploading your media.
        </p>
        <form>
          <div className="auth-input-container">
            <input type="email" name="email" placeholder="Email" className="auth-input" required />
            <i className="bx bx-envelope"></i>
          </div>
          <div className="auth-input-container">
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              className="auth-input"
              required
            />
            <i
              className={`bx ${showPassword ? "bx-show" : "bx-hide"}`}
              onClick={() => setShowPassword(!showPassword)}
              style={{ cursor: "pointer" }}
            ></i>
          </div>
          <a href="/forgot-password" className="auth-recovery">Forgot Password?</a>
          <button type="submit" className="auth-button">Sign In</button>
        </form>
      </div>
      <div className="auth-illustration">StampNet Logo</div>
    </div>
  );
};

export default Login;
