import React, { useState } from "react";
import "boxicons/css/boxicons.min.css";

const AuthRegister = () => {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [passwordValidations, setPasswordValidations] = useState({
    lowercase: false,
    uppercase: false,
    number: false,
    length: false,
  });

  const handlePasswordChange = (e) => {
    const value = e.target.value;
    setPassword(value);

    setPasswordValidations({
      lowercase: /[a-z]/.test(value),
      uppercase: /[A-Z]/.test(value),
      number: /[0-9]/.test(value),
      length: value.length >= 8,
    });
  };

  const checkConfirmPassword = () => {
    if (!confirmPassword) return "";
    return password === confirmPassword ? "‚úÖ Passwords match" : "‚ùå Passwords do not match";
  };

  return (
    <div className="AuthRegister-container">
      <div className="AuthRegister-box">
        <h2 className="AuthRegister-title">Create An Account!</h2>
        <form className="AuthRegister-form">
          <div className="AuthRegister-input-container">
            <input type="text" name="fname" placeholder="Full Name" required />
          </div>
          <div className="AuthRegister-input-container">
            <input type="email" name="email" placeholder="Email" required />
            <i className="bx bx-envelope icon"></i>
          </div>
          <div className="AuthRegister-input-container">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
          </div>
          <div className="AuthRegister-password-validation">
            {Object.entries(passwordValidations).map(([key, isValid]) => (
              <p key={key} className={isValid ? "valid" : "invalid"}>
                {isValid ? "‚úÖ" : "üî¥"} {key.charAt(0).toUpperCase() + key.slice(1)}
              </p>
            ))}
          </div>
          <div className="AuthRegister-input-container">
            <input
              type="password"
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
            <p className="AuthRegister-password-match">{checkConfirmPassword()}</p>
          </div>
          <button type="submit" className="AuthRegister-btn">Sign Up</button>
        </form>
      </div>
    </div>
  );
};

export default AuthRegister;
