import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import HeroSection from "./components/HeroSection";
import RegisterPage from "./pages/RegisterPage";
import LoginPage from "./pages/LoginPage";
import ForgotPasswordPage from "./pages/ForgotPasswordPage";
import useScrollBlur from "./hooks/useScrollBlur"; // Import the scroll blur effect
import "./styles/global.css"; // Import the global CSS for blur effect

function App() {
  useScrollBlur(); // Apply the blur effect on scroll

  return (
    <Router>
      <div className="main-content"> {/* Wrap everything to apply blur effect */}
        <Routes>
          {/* Landing Page with Navbar and Footer */}
          <Route
            path="/"
            element={
              <>
                <Navbar />
                <HeroSection />
                <Footer />
              </>
            }
          />

          {/* Auth Pages (No Navbar or Footer) */}
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/forgot-password" element={<ForgotPasswordPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
