import React, { useState } from "react";
import "../../styles/authstyle.css"; 

const LoginPage = () => {
  const [passwordVisible, setPasswordVisible] = useState(false);

  const togglePasswordVisibility = () => {
    setPasswordVisible(!passwordVisible);
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2 className="text-center">Hello Again!</h2>
        <p className="text-center text-muted">Let's get started with uploading your media</p>

        <form method="post">
          <div className="input-container">
            <input type="email" name="email" placeholder="Email" required />
          </div>

          <div className="input-container">
            <input type={passwordVisible ? "text" : "password"} id="password" placeholder="Password" required />
            <span className="toggle-icon" onClick={togglePasswordVisibility}>
              {passwordVisible ? "üëÅÔ∏è" : "üôà"}
            </span>
          </div>

          <a href="/forgot-password" className="recovery">Forgot Password?</a>
          <button type="submit" className="button">Sign In</button>
        </form>
      </div>

      <div className="illustration">
        <img src="#" alt="Illustration" className="illustration-img" />
      </div>
    </div>
  );
};

export default LoginPage;
