import React, { useState } from "react";
import "boxicons/css/boxicons.min.css";

const Register = () => {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [passwordValidations, setPasswordValidations] = useState({
    lowercase: false,
    uppercase: false,
    number: false,
    length: false,
  });

  const handlePasswordChange = (e) => {
    const value = e.target.value;
    setPassword(value);

    setPasswordValidations({
      lowercase: /[a-z]/.test(value),
      uppercase: /[A-Z]/.test(value),
      number: /[0-9]/.test(value),
      length: value.length >= 8,
    });
  };

  const checkConfirmPassword = () => {
    if (!confirmPassword) return "";
    return password === confirmPassword ? "‚úÖ Passwords match" : "‚ùå Passwords do not match";
  };

  return (
    <div className="container d-flex flex-column flex-md-row overflow-hidden">
      <div className="login-box p-4 w-100 w-md-50 d-flex flex-column justify-content-center">
        <h2 className="text-center">Create An Account!</h2>
        <form>
          <div className="input-container mb-3">
            <input type="text" name="fname" placeholder="Full Name" className="form-control" required />
          </div>
          <div className="input-container mb-3">
            <input type="email" name="email" placeholder="Email" className="form-control" required />
            <i className="bx bx-envelope icon"></i>
          </div>
          <div className="input-container mb-3">
            <input
              type="password"
              placeholder="Password"
              className="form-control"
              value={password}
              onChange={handlePasswordChange}
              required
            />
          </div>
          <div className="mb-3">
            {Object.entries(passwordValidations).map(([key, isValid]) => (
              <p key={key} className={`password-message-item ${isValid ? "text-success" : "text-danger"}`}>
                {isValid ? "‚úÖ" : "üî¥"} {key.charAt(0).toUpperCase() + key.slice(1)}
              </p>
            ))}
          </div>
          <div className="input-container mb-3">
            <input
              type="password"
              placeholder="Confirm Password"
              className="form-control"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
            <p className="mt-2">{checkConfirmPassword()}</p>
          </div>
          <button type="submit" className="btn btn-primary w-100">Sign Up</button>
        </form>
      </div>
      <div className="illustration w-50 bg-primary d-none d-md-flex align-items-center justify-content-center text-white">
        StampNet Logo
      </div>
    </div>
  );
};

export default Register;
