import React from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import HeroSection from "./components/HeroSection";
import RegisterPage from "./pages/RegisterPage";
import LoginPage from "./pages/LoginPage";
import ForgotPasswordPage from "./pages/ForgotPasswordPage";
import AboutUs from "./pages/AboutUs"; // Import AboutUs Page
import Slider from "./components/Slider"; 
import DashboardLayout from "./layouts/DashboardLayout"; // Import Dashboard Layout
import DashboardPage from "./pages/DashboardPage"; // Import Dashboard Page
import "./styles/global.css"; // Ensure global styles are applied

function App() {
  return (
    <Router>
      <MainContent />
    </Router>
  );
}

const MainContent = () => {
  const location = useLocation();
  const authPages = ["/register", "/login", "/forgot-password"];
  const isAuthPage = authPages.includes(location.pathname);
  const isDashboard = location.pathname.startsWith("/dashboard"); // Check if it's dashboard

  return (
    <>
      {/* Show Navbar only if it's NOT an auth page or dashboard */}
      {!isAuthPage && !isDashboard && <Navbar />}

      <div className="main-content">
        <Routes>
          {/* Landing Page */}
          <Route
            path="/"
            element={
              <>
                <HeroSection />
                <Slider />
                <Footer />
              </>
            }
          />

          {/* About Us Page (With Navbar & Footer) */}
          <Route
            path="/about-us"
            element={
              <>
                <AboutUs />
                <Footer />
              </>
            }
          />

          {/* Auth Pages (No Navbar or Footer) */}
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/forgot-password" element={<ForgotPasswordPage />} />

          {/* Dashboard (Different Layout) */}
          <Route
            path="/dashboard"
            element={
              <DashboardLayout>
                <DashboardPage />
              </DashboardLayout>
            }
          />
        </Routes>
      </div>
    </>
  );
};

export default App;
