import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "../styles/Slider.css"; // Custom styling

const slides = [
  {
    id: 1,
    image: "images/img1.jpeg", // Replace with actual image path
    title: "Prove",
    content:
      "Civic stores and manages credentials, issuing on-chain attestations as needed.",
  },
  {
    id: 2,
    image: "/images/slide2.jpg",
    title: "Connect",
    content:
      "Let users sign in with email, Google, passkeys, or their wallet. Civic Auth adapts to your preference.",
  },
  {
    id: 3,
    image: "/images/slide3.jpg",
    title: "Verify",
    content:
      "Verify Proofs of personhood and liveness, without revealing private information.",
  },
];

const Slider = () => {
  return (
    <div className="slider-container">
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={30}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        autoplay={{ delay: 5000 }}
        loop={true}
      >
        {slides.map((slide) => (
          <SwiperSlide key={slide.id}>
            <div className="slide-content">
              <img src={slide.image} alt={slide.title} className="slide-image" />
              <h2 className="slide-title">{slide.title}</h2>
              <p className="slide-text">{slide.content}</p>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default Slider;
