import React, { useState } from "react";
import "boxicons/css/boxicons.min.css";
import "bootstrap/dist/css/bootstrap.min.css";

const Login = () => {
  const [showPassword, setShowPassword] = useState(false);

  return (
    <div className="container d-flex flex-column flex-md-row overflow-hidden">
      <div className="login-box p-4 w-100 w-md-50 d-flex flex-column justify-content-center">
        <h2 className="text-center">Hello Again!</h2>
        <p className="text-center text-muted">Let's get started with uploading your media</p>
        <form>
          <div className="input-container mb-3">
            <input type="email" name="email" placeholder="Email" className="form-control" required />
            <i className="bx bx-envelope"></i>
          </div>
          <div className="input-container mb-3">
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              className="form-control"
              required
            />
            <i
              className={`bx ${showPassword ? "bx-show" : "bx-hide"}`}
              onClick={() => setShowPassword(!showPassword)}
              style={{ cursor: "pointer" }}
            ></i>
          </div>
          <a href="/forgot-password" className="recovery">Forgot Password?</a>
          <button type="submit" className="btn btn-primary w-100 mt-3">Sign In</button>
        </form>
      </div>
      <div className="illustration w-50 bg-primary d-none d-md-flex align-items-center justify-content-center text-white">
        StampNet Logo
      </div>
    </div>
  );
};

export default Login;
