import React, { useState } from "react";
import "../components/styles/Slider.css"; // Import the CSS

const slides = [
  {
    src: "/images/img1.jpeg",
    header: "Prove",
    content: "Civic stores and manages credentials, issuing on-chain attestations as needed...",
  },
  {
    src: "/images/img1.jpeg",
    header: "Connect",
    content: "Let users sign in with email, Google, passkeys, or their wallet...",
  },
  {
    src: "/images/img1.jpeg",
    header: "Verify",
    content: "Verify Proofs with zero-knowledge technology, ensuring user privacy...",
  },
];

const Slider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + slides.length) % slides.length);
  };

  return (
    <div className="slider-container">
      <button className="slider-btn prev" onClick={prevSlide}>❮</button>

      <div className="slider-wrapper">
        {slides.map((slide, index) => (
          <div 
            key={index} 
            className={`slide ${index === currentIndex ? "active" : "hidden"}`}
          >
            <img src={slide.src} alt="Slide" className="slider-image" />
            <h2 className="slider-header">{slide.header}</h2>
            <p className="slider-text">{slide.content}</p>
          </div>
        ))}
      </div>

      <button className="slider-btn next" onClick={nextSlide}>❯</button>
    </div>
  );
};

export default Slider;
