import React, { useState } from "react";
import "../../styles/authstyle.css"; 
import "boxicons/css/boxicons.min.css";

const Register = () => {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const [passwordValidations, setPasswordValidations] = useState({
    lowercase: false,
    uppercase: false,
    number: false,
    length: false,
  });

  const handlePasswordChange = (e) => {
    const value = e.target.value;
    setPassword(value);

    setPasswordValidations({
      lowercase: /[a-z]/.test(value),
      uppercase: /[A-Z]/.test(value),
      number: /[0-9]/.test(value),
      length: value.length >= 8,
    });
  };

  const checkConfirmPassword = () => {
    if (!confirmPassword) return "";
    return password === confirmPassword ? "‚úÖ Passwords match" : "‚ùå Passwords do not match";
  };

  return (
    <div className="Authregister-container">
      <div className="Authregister-box">
        <h2 className="Authregister-title">Create An Account</h2>
        <p className="Authregister-subtitle">Let's get started with uploading your media.</p>

        <form className="Authregister-form">
          {/* Full Name */}
          <div className="Authregister-input-group">
            <input type="text" name="fname" placeholder="Full Name" className="Authregister-input" required />
          </div>

          {/* Email */}
          <div className="Authregister-input-group">
            <input type="email" name="email" placeholder="Email" className="Authregister-input" required />
            <i className="bx bx-envelope Authregister-icon"></i>
          </div>

          {/* Password */}
          <div className="Authregister-input-group">
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              className="Authregister-input"
              value={password}
              onChange={handlePasswordChange}
              required
            />
            <i
              className={`bx ${showPassword ? "bx-hide" : "bx-show"} Authregister-icon`}
              onClick={() => setShowPassword(!showPassword)}
            ></i>
          </div>

          {/* Password Requirements */}
          <div className="Authregister-password-validations">
            {Object.entries(passwordValidations).map(([key, isValid]) => (
              <p key={key} className={`Authregister-password-msg ${isValid ? "valid" : "invalid"}`}>
                {isValid ? "‚úÖ" : "üî¥"} {key.charAt(0).toUpperCase() + key.slice(1)}
              </p>
            ))}
          </div>

          {/* Confirm Password */}
          <div className="Authregister-input-group">
            <input
              type={showConfirmPassword ? "text" : "password"}
              placeholder="Confirm Password"
              className="Authregister-input"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
            <i
              className={`bx ${showConfirmPassword ? "bx-hide" : "bx-show"} Authregister-icon`}
              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
            ></i>
          </div>

          <p className="Authregister-confirm-msg">{checkConfirmPassword()}</p>

          {/* Submit Button */}
          <button type="submit" className="Authregister-button">Sign Up</button>
        </form>

        <p className="Authregister-footer">
          Already have an account? <a href="/login">Sign In</a>
        </p>
      </div>
    </div>
  );
};

export default Register;
